function initializeFormHandler(){const e=document.getElementById("contactEmail");e&&e.addEventListener("submit",function(t){t.preventDefault();const n=new FormData(e);fetch("send-email.php",{method:"POST",body:n}).then(e=>e.text()).then(t=>{"success"===t.trim()?(alert("Pesan berhasil dikirim!"),e.reset()):alert("Gagal mengirim pesan.\n"+t)}).catch(e=>{alert("Terjadi kesalahan koneksi.")})})}
